(()=>{const e=document.querySelector("#hamburger"),s=document.querySelector(".main");function t(){window.matchMedia("(min-width: 800px)").matches?(s.classList.remove("sidebar-toggle"),e.classList.add("change")):(s.classList.add("sidebar-toggle"),e.classList.remove("change"))}e.addEventListener("click",(()=>{s.classList.toggle("sidebar-toggle"),e.classList.toggle("change")})),window.addEventListener("resize",(()=>{t()})),t()})();